mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT a.applicant_no, a.applicant_status, a.reg_date, p.project_name, p.project_no, p.project_detail, p.user_id, p.application_deadline, p.recruitment_member_cnt, p.project_status, p.meeting_location FROM applicant a, project p WHERE a.project_no = p.project_no AND a.user_id = #{userId}
AND a.REG_DATE BETWEEN TRUNC(ADD_MONTHS(sysdate,-1)+1) - TO_CHAR(sysdate,'DD') AND TRUNC(LAST_DAY(sysdate)) +0.99999421 AND a.REG_DATE BETWEEN TRUNC(ADD_MONTHS(sysdate,-2)+1) - TO_CHAR(sysdate,'DD') AND TRUNC(LAST_DAY(sysdate)) +0.99999421 AND a.REG_DATE BETWEEN TRUNC(ADD_MONTHS(sysdate,-3)+1) - TO_CHAR(sysdate,'DD') AND TRUNC(LAST_DAY(sysdate)) +0.99999421
AND a.applicant_status = 1 AND a.applicant_status = 2 AND a.applicant_status = 3 AND a.applicant_status = 4 AND a.applicant_status = 6
ORDER BY a.APPLICANT_NO ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT a.applicant_no, a.applicant_status, a.reg_date FROM applicant a, project p WHERE a.project_no = p.project_no AND a.user_id = #{userId}
AND a.applicant_status = 1
AND a.applicant_status = 2
AND a.applicant_status = 3 AND a.applicant_status = 4
) countTable

mapper.update:
UPDATE MEMBER
PROJECT_NO = (SELECT PROJECT_NO FROM APPLICANT WHERE APPLICANT_NO=#{applicantNo})
WHERE USER_ID = #{userId}
